drop table  if exists store;
create table store (id bigint NOT NULL auto_increment, store_name varchar(50),store_desc varchar(50),store_ad_pic varchar(100),store_type varchar(20),add_ress varchar(100),store_domain varchar(100),store_tel varchar(50),operation_time_begin int default 0, operation_time_end int default 0,store_status int default 0,approval_status int default 0,sign_status int default 0,store_score int default 0,total_browse_count int default 0,lon double default 0.00,lat double default 0.00, primary key (id));